var PortViewRender = {
  GetData: function GetData() {
    getQueryString();
    var baseURI = 'api/get.portfolio.php';
    axios.post(baseURI, params).then(function (result) {
      console.log(result);
      var tabMenu = document.querySelectorAll('.tab_menu');
      var viewTit = document.getElementById('sub_tit');
      var viewHead = document.getElementById('view_head');
      var viewCon = document.getElementById('view_con');
      var PortNav = document.getElementById('port_nav');

      if (result.data.phpResult == 'ok') {
        var GetData = result.data.result[0];

        if (GetData.standard == "주거") {
          tabMenu[0].className = 'on';
        } else if (GetData.standard == "상업") {
          tabMenu[1].className = 'on';
        } else if (GetData.standard == "사무") {
          tabMenu[2].className = 'on';
        }

        viewTit.innerHTML = "<h1>".concat(GetData.standard, "\uACF5\uAC04</h1>");
        viewHead.innerHTML = "\n                    <p>".concat(GetData.tit, "</p>\n                    <ul>\n                        <li>\uC2DC\uACF5\uD615\uD0DC : ").concat(GetData.standard, "\uACF5\uAC04</li>\n                        <li>\uC2DC\uACF5\uC8FC\uC18C : ").concat(GetData.reqAddress, "</li>\n                        <li>\uC2DC\uACF5\uBA74\uC801 : ").concat(GetData.reqMeasure, "</li>\n                        <li>\uCC9C\uC815 : ").concat(GetData.option1, "</li>\n                        <li>\uBC14\uB2E5 : ").concat(GetData.option2, "</li>\n                        <li>\uBCBD\uCCB4 : ").concat(GetData.option3, "</li>\n                    </ul>");
        viewCon.innerHTML = GetData.Desc;
        var Standards = result.data.Standardidx;
        var Standardidx = [];

        for (var i = 0; i < Standards.length; i++) {
          Standardidx.push(Standards[i].idx);
        }

        var thisIndex = Standardidx.indexOf(params.idx);
        var NextPage = thisIndex == Standardidx.length - 1 ? 0 : thisIndex + 1;
        var AfterPage = thisIndex == 0 ? Standardidx.length - 1 : thisIndex - 1;
        PortNav.innerHTML = "<a href=\"portfolio_view.html?idx=".concat(Standardidx[AfterPage], "\" class=\"fl\"><img src=\"images/navi_priv2.png\" alt=\"\"></a>\n                    <a href=\"portfolio.html#/\" class=\"btn_list\">\uBAA9\uB85D\uBCF4\uAE30</a>\n                    <a href=\"portfolio_view.html?idx=").concat(Standardidx[NextPage], "\" class=\"fr\"><img src=\"images/navi_next2.png\" alt=\"\"></a>");
      }
    }).catch(function (err) {
      return console.log('Login: ', err);
    });
  }
};

